function workTemplate(i) {
  const template = document.querySelector('#Works-Template');
  const clone = document.importNode(template.content, true);
  clone.querySelector('.works__img img').setAttribute('src', i.srcimgM);
  clone.querySelector('.works__img img').setAttribute('alt', i.alt);
  shadowImg.appendChild(clone);
}
let numberОfСallsBtnMore = 1;
const shadowImg = document.querySelector('.works__cards');
function MaxElem(numberОfСallsBtn, filterClass, allbtnmore) {
  const amountOfElements = numberОfСallsBtn * 3;
  const getMaxelement = filterItem(filterClass, amountOfElements, allbtnmore);
  return getMaxelement;
}

function findName(filterClass = 'ALL', viewMore, allbtnmore) {
  filterClass = filterClass.replace(/[/]/g, '');
  let getMaxelement = null;
  const worksCard = document.querySelectorAll('.works__card ');
  worksCard.forEach((element) => {
    element.remove();
  });
  if (viewMore) {
    ++numberОfСallsBtnMore;
    getMaxelement = MaxElem(numberОfСallsBtnMore, filterClass, allbtnmore);
    if (getMaxelement) { numberОfСallsBtnMore = 0; btnWorksBottom.textContent = 'VIEW LESS'; } else { btnWorksBottom.textContent = 'VIEW MORE'; }
  } else {
    filterItem(filterClass, 3, true);
    btnWorksBottom.textContent = 'VIEW MORE';
    numberОfСallsBtnMore = 1;
  }
}

function filterItem(filterClass, amountOfElements, loadingAPage) {
  let maxelement = false;
  if (loadingAPage && filterClass !== 'ALL') {
    for (const key in worksImg) {
      if (filterClass === key) {
        if (amountOfElements > worksImg[key].length) {
          amountOfElements = worksImg[key].length;
          maxelement = true;
        }
        for (let j = 0; j < amountOfElements; j++) {
          workTemplate(worksImg[key][j]);
        }
      }
    }
  } else {
    for (const key in worksImg) {
      if (amountOfElements > worksImg[key].length) {
        amountOfElements = worksImg[key].length;
        maxelement = true;
      }
      for (let i = 0; i < amountOfElements; i++) {
        workTemplate(worksImg[key][i]);
      }
    }
  }
  return maxelement;
}
let filterClass = 'ALL';
filterItem('ALL', 3, false);
const btns = document.querySelectorAll('.btn__works-1');
btns.forEach((btnRead) => {
  btnRead.addEventListener('click', () => {
    filterClass = btnRead.getAttribute('data-type');
    btns.forEach((btn) => {
      btn.classList.remove('btn-active');
    });
    btnRead.classList.add('btn-active');
    findName(filterClass, false);
  });
});
const btnWorksBottom = document.querySelector('.btn__works-bottom');
btnWorksBottom.addEventListener('click', () => {
  const viewMore = true;
  if (filterClass === 'ALL') {
    findName(filterClass, viewMore, false);
  } else {
    findName(filterClass, viewMore, true);
  }
});


